/* eslint-disable array-callback-return */
/* eslint-disable jsx-a11y/alt-text */
import React, { useState } from 'react'
import Category from '../Category/Category'
import Skill from '../Skill/Skill'
import Service from '../Service/Service'
import './style.css'

const Advisor = ({ items }) => {
  const [view, setView] = useState('list__item__vertical')
  const [style,setStyle]=useState('list__item__vertical')
  const [item, setItem] = useState('')
  const handleOnClick = () =>{
    if(view==='list__item__vertical')
    {
      setView('list__item__vertical')
    }

  }
  return (
    <>
      <div className="feature">
        <button onClick={handleOnClick}>{view}</button>
        <div className='feature__search'>
          <input type="text" placeholder='Search' onChange={(e) => setItem(e.target.value)} />
        </div>
      </div>
      <div className={view}>
        {items.map((item, index) => {
          if (item.avatarUrl && item.avatarUrl.url) {
            return (
              <div key={index} className="item">
                <div className='item__img'>
                  <img key={index} src={item.avatarUrl.url}></img>
                </div>
                <div className='item__content'>
                  <div className='item__content__information'>
                    <h3>Thông tin</h3>
                    <div className='item__information__name'>Tên: {item.displayName}</div>
                    <div>Email: {item.email}</div>
                    <div>Sđt: {item.phone}</div>
                  </div>
                  <div className='item__content__category'>
                    <h3>Doanh mục</h3>
                    <Category categories={item.categoriesCollection.items} />
                  </div>
                  <div className='item__content__skill'>
                    <h3>Kỹ năng</h3>
                    <Skill skills={item.skillsCollection.items} />
                  </div>
                  <div className='item__content__service'>
                    <h3>Dịch vụ</h3>
                    <Service services={item.servicesCollection.items} />
                  </div>
                </div>
              </div>
            )
          }
        })}
      </div>
    </>
  )
}
export default Advisor
