/* eslint-disable array-callback-return */
/* eslint-disable jsx-a11y/alt-text */
import React, { useState, useRef } from 'react'
import Category from '../Category/Category'
import Skill from '../Skill/Skill'
import Service from '../Service/Service'
import './style.css'

const Advisor = ({ items }) => {
  const arr = []
  const [isVertical, setIsVertical] = useState(false)
  const [values, setValues] = useState(items)
  const inputRef = useRef()

  const handleOnClick = () => {
    const value = inputRef?.current.value
    const cate = []
    const inputName = items.filter(findName => (
      findName.displayName.indexOf(value) !== -1
    ))
    const inputCategory = items.map(item1 => {
      item1.categoriesCollection.items.filter(item2 => {
        if (item2.displayName.indexOf(value) !== -1) {
          return cate.push(item1);
        }
      })
    })
    const result = cate.reduce(function (accumulator, element) {
      if (accumulator.indexOf(element) === -1) {
        accumulator.push(element)
      }
      return accumulator
    }, [])
    if (value.trim() === '') return setValues(items);
    if (inputName.length === 0 ? setValues(inputName) : setValues(inputName));
    if (result.length === 0 ? setValues(inputName) : setValues(result));
  }
  return (
    <>
      <div className="feature">
        <button onClick={() => setIsVertical(pre => !pre)}>{isVertical ? "Vertical" : "Horizontal"}</button>
        <div className='feature__search'>
          <input ref={inputRef} type="text" placeholder='Search' />
          <button onClick={(e) => { handleOnClick(e.target.value) }}>Search</button>
        </div>
      </div>
      <div className={`${isVertical ? "list__item__horizontal" : "list__item__vertical"}`}>
        {values.map((item, index) => {
          if (item.avatarUrl && item.avatarUrl.url) {
            return (
              <div key={index} className="item">
                <div className='item__img'>
                  <img key={index} src={item.avatarUrl.url}></img>
                </div>
                <div className='item__content'>
                  <div className='item__content__information'>
                    <h3>Thông tin</h3>
                    <div className='item__information__name'>Tên: {item.displayName}</div>
                    <div>Email: {item.email}</div>
                    <div>Sđt: {item.phone}</div>
                  </div>
                  <div className='item__content__category'>
                    <h3>Doanh mục</h3>
                    <Category categories={item.categoriesCollection.items} />
                  </div>
                  <div className='item__content__skill'>
                    <h3>Kỹ năng</h3>
                    <Skill skills={item.skillsCollection.items} />
                  </div>
                  <div className='item__content__service'>
                    <h3>Dịch vụ</h3>
                    <Service services={item.servicesCollection.items} />
                  </div>
                </div>
              </div>
            )
          }
        })}
      </div>
    </>
  )
}
export default Advisor
