/* eslint-disable array-callback-return */
/* eslint-disable jsx-a11y/alt-text */
import React, { useState } from 'react'
import Category from '../Category/Category'
import './style.css'

const Advisor = ({ items }) => {
  const [view, setView] = useState('list__item__vertical')
  return (
    <>
      <button onClick={() => setView('list__item__vertical')}>Vertical view</button>
      <button onClick={() => setView('list__item__horizontal')}>Horizontal view</button>
      <div className={view}>
        {items.map((item, index) => {
          if (item.avatarUrl && item.avatarUrl.url) {
            return (
              <div className="item">
                <div className='item__img'>
                  <img key={index} src={item.avatarUrl.url}></img>
                </div>
                <div className='item__content'>
                  <div className='item__content__information'>
                    <h3>Thông tin</h3>
                    <div className='item__information__name'>Tên: {item.displayName}</div>
                    <div>Email: {item.email}</div>
                    <div>Sđt: {item.phone}</div>
                  </div>
                  <div className='item__content__category'>
                    <h3>Doanh mục</h3>
                    <Category categories={item.categoriesCollection.items} />
                  </div>
                </div>
              </div>
            )
          }
        })}
      </div>
    </>
  )
}
export default Advisor
