import { useRef, useState } from "react";
import './Filter.css'
import Button from "../Button/Button";
const Form = (props) => {
  const [enteredName,setEnteredName] = useState('')
  const [enteredCategory, setEnteredCategory] = useState('')
  const onNameChangeHandler = event =>{
    if(event.target.value===''){
      props.restoreDefault()
      return
    }
    setEnteredName(event.target.value)
  }

  const onCategoryChangeHandler = event =>{
    if(event.target.value===''){
      props.restoreDefault()
      return
    }
    setEnteredCategory(event.target.value)
  }

  const onClearSearchFields = (event) =>{
    setEnteredName('')
    setEnteredCategory('')
  }
  
  return (
    <form
      onSubmit={(event) => {
        event.preventDefault();
        props.onSearch(enteredName, enteredCategory);
      }} className='filter'
    >
      <div className="form">
        <div className="form-element">
          <label>Name</label>
          <input type="text" name="Name" value={enteredName}  onChange={onNameChangeHandler}></input>
        </div>
        <br></br>
        <div className="form-element">
          <label>Category</label>
          <input type="text" name="Category" value={enteredCategory}  onChange={onCategoryChangeHandler}></input>
        </div>
      </div>
      
      <br></br>
      <div className="buttons">
        <Button text='Search'></Button>
        <Button text='Clear' onClick={onClearSearchFields}></Button>
      </div>
    </form>
  );
};
export default Form;
