import { useRef } from "react";
import './Filter.css'
import Button from "../Button/Button";
const Form = (props) => {
  const name = useRef();
  const category = useRef();

  const onChangeHandler = event =>{
    if(event.target.value===''){
      props.restoreDefault()
    }
  }

  const onClearSearchFields = (event) =>{
    
  }
  
  return (
    <form
      onSubmit={(event) => {
        event.preventDefault();
        props.onSearch(name.current.value, category.current.value);
      }} className='filter'
    >
      <div className="form">
        <div className="form-element">
          <label>Name</label>
          <input type="text" name="Name" ref={name} onChange={onChangeHandler}></input>
        </div>
        <br></br>
        <div className="form-element">
          <label>Category</label>
          <input type="text" name="Category" ref={category} onChange={onChangeHandler}></input>
        </div>
      </div>
      
      <br></br>
      <div className="buttons">
        <Button text='Search'></Button>
        <Button text='Clear' onClick={onClearSearchFields}></Button>
      </div>
    </form>
  );
};
export default Form;
