import { useRef } from "react";
const Form = (props) => {
  const name = useRef();
  const category = useRef();

  const onChangeHandler = event =>{
    if(event.target.value===''){
      props.restoreDefault()
    }
  }

  const onClearSearchFields = (event) =>{
    console.log("clear")
  }
  
  return (
    <form
      onSubmit={(event) => {
        event.preventDefault();
        props.onSearch(name.current.value, category.current.value);
      }}
    >
      <label>Name</label>
      <input type="text" name="Name" ref={name} onChange={onChangeHandler} value={name}></input>
      <br></br>
      <label>Category</label>
      <input type="text" name="Category" ref={category} onChange={onChangeHandler} value={category}></input>
      <br></br>
      <button>Search</button>
      <button onClick={onClearSearchFields}>Clear</button>
    </form>
  );
};
export default Form;
