import { useRef } from "react";
const Form = (props) => {
  const name = useRef();
  const category = useRef();

  const onNameChangeHandler = event =>{
    if(event.target.value===''){
      props.restoreDefault()
    }
  }
  
  return (
    <form
      onSubmit={(event) => {
        event.preventDefault();
        props.onSearch(name.current.value, category.current.value);
      }}
    >
      <label>Name</label>
      <input type="text" name="Name" ref={name} onChange={onNameChangeHandler}></input>
      <br></br>
      <label>Category</label>
      <input type="text" name="Category" ref={category}></input>
      <br></br>
      <button>Search</button>
    </form>
  );
};
export default Form;
