import React, { useState } from "react";
import "./App.css";
import { advisorsData } from "./components/data/advisors";
import PersonList from "./components/Info/PersonList";
import Filter from "./components/FilterForm/Filter";

const FULL_LIST = advisorsData[0].data.advisorProfileCollection.items;
const App = () => {
  // console.log(data)
  const [list, setFilteredList] = useState(FULL_LIST);

  const data = advisorsData[0].data.advisorProfileCollection.items;
  console.log(data[0].displayName);

  const onSearch = (name, category) => {
    console.log(name, category);

    const filteredList = list.filter((person) => person.displayName === name);
    setFilteredList(filteredList);
  };
  return (
    <React.Fragment>
      <h1 align="center">Information about advisors</h1>
      <Filter onSearch={onSearch}></Filter>
      <PersonList list={list}></PersonList>
    </React.Fragment>
  );
};

export default App;

